const burger=document.querySelector(".menu-icon"),menu=document.querySelector(".menu"),body=document.body;if(burger&&menu){burger.addEventListener("click",()=>{burger.classList.toggle("_active"),menu.classList.toggle("_active"),body.classList.toggle("_lock")});menu.querySelectorAll("a").forEach(e=>{e.addEventListener("click",()=>{burger.classList.remove("_active"),menu.classList.remove("_active"),body.classList.remove("_lock")})})}function setTheme(e){localStorage.setItem("theme",e),document.documentElement.className=e}function toggleTheme(){"theme-dark"===localStorage.getItem("theme")?setTheme("theme-light"):setTheme("theme-dark")}"theme-dark"===localStorage.getItem("theme")?(setTheme("theme-dark"),document.getElementById("slider").checked=!1):(setTheme("theme-light"),document.getElementById("slider").checked=!0),document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".section").forEach((function(e){e.addEventListener("click",(function(){this.scrollIntoView({behavior:"smooth"})}))}))}));